mapper.insert:
		INSERT INTO test.tbl_product
        (
		  product_id
		, product_name
		, product_price
		, product_desc
		, product_url
		)
		VALUES (
		seq_product_id.NEXTVAL
		, #{productName}
		, #{productPrice}
		, #{productDesc}
		, #{productUrl}
		)

mapper.update:
UPDATE test.tbl_product
SET product_name = #{productName}
, product_price = #{productPrice}
, product_desc = #{productDesc}
, product_url = #{productUrl}
WHERE
product_id = #{productId}
